import type { Metadata } from "next";
import { Geist, Geist_Mono } from "next/font/google";
import "./globals.css";
import ThemeToggle from "@/components/theme-toggle";

const geistSans = Geist({ variable: "--font-geist-sans", subsets: ["latin"] });
const geistMono = Geist_Mono({ variable: "--font-geist-mono", subsets: ["latin"] });

export const metadata: Metadata = {
  title: "BB Fireworks, Nilganj",
  description: "BB Fireworks showroom at Nilganj",
};

export default function RootLayout({ children }: { children: React.ReactNode }) {
  return (
    <html lang="en" suppressHydrationWarning>
      <head>
        {/* Set theme ASAP to avoid flash */}
        <script
          dangerouslySetInnerHTML={{
            __html: `
              try {
                const ls = localStorage.getItem('theme');
                const mql = window.matchMedia('(prefers-color-scheme: dark)');
                const wantDark = ls ? ls === 'dark' : mql.matches;
                const root = document.documentElement;
                if (wantDark) root.classList.add('dark');
                else root.classList.remove('dark');
              } catch {}
            `,
          }}
        />
      </head>

      <body className={`${geistSans.variable} ${geistMono.variable} antialiased min-h-screen`}>
        <a href="#content" className="sr-only focus:not-sr-only">Skip to content</a>

        <header className="border-b border-gray-200 dark:border-gray-800 p-3 flex items-center justify-between">
          <div className="font-semibold">BB Fireworks</div>
          <ThemeToggle />
        </header>

        <main id="content" className="container mx-auto p-4">
          {children}
        </main>
      </body>
    </html>
  );
}